mapper.select:
		
		select b.*
		from (
		    select rownum N, a.*, u.user_name
		    from (
		        select *
		        from tbl_board order by brd_no desc
		        ) a, tbl_users u
		    where a.brd_writer = u.user_id
		    ) b
		where b.N > (#{page} - 1) * 10

		and b.N <= #{page} * 10
